import React from 'react';
import { Form, Radio, Button, Upload, Steps, message, Progress, Image, Tabs, Table } from 'antd';
import { UploadOutlined, InboxOutlined } from '@ant-design/icons';
import { withRouter } from 'react-router-dom';
import { connect } from "react-redux";
import { exportExcel } from '../../utils/excel'
import axios from 'axios';
import Bar from '../Bar'
import Time from '../Time'
import defectStyles from './index.module.less'

const { Step } = Steps;
const { TabPane } = Tabs;

const formItemLayout = {
    labelCol: { span: 6 },
    wrapperCol: { span: 14 },
};

const normFile = (e) => {
    console.log('Upload event:', e);

    if (Array.isArray(e)) {
        return e;
    }

    return e && e.fileList;
};

var type = [];
var confidence = [];

var option = {
    tooltip: {
        trigger: 'axis',
        axisPointer: {
            type: 'shadow'
        }
    },
    grid: {
        left: '3%',
        right: '4%',
        bottom: '3%',
        containLabel: true
    },
    xAxis: [
        {
            type: 'category',
            data: type,
            axisTick: {
                alignWithLabel: true
            }
        }
    ],
    yAxis: [
        {
            type: 'value'
        }
    ],
    series: [
        {
            name: '置信度',
            type: 'bar',
            barWidth: '60%',
            data: confidence
        }
    ]
};

function DefectDete() {

    //步骤条
    const [current, setCurrent] = React.useState(0);
    //进度
    const [percent, setPercent] = React.useState(0);

    const prev = () => {
        if (current === 2) {
            setPercent(0);
            type = [];
            confidence = [];
        }    
        if (current === 1) {
            setIsLightImg(false);
            setIsMicroImg(false);
            setIsWay(false);
        }
        setCurrent(current - 1);
    };

    const handleWay = e => {
        setWay(e.target.value);
        setIsWay(true);
    }

    const onFinish = (values) => {
        if (current === 0) {
            defectDete();
            setTimeout(() => {
                setPercent(25);
                setTimeout(() => {
                    setPercent(50);
                    setTimeout(() => {
                        setPercent(75);
                        setTimeout(() => {
                            setPercent(100);
                        }, 3000);
                    }, 3000);
                }, 3000);
            }, 3000);
        }
        setCurrent(current + 1);
        console.log('接收到的表单值为: ', values);
        console.log('选择的检测方法是：', values['radio-group']);
    };

    //1.选择文件
    //原可见光图像
    const [lightImg, setLightImg] = React.useState('');
    //是否选择了可见光图像
    const [isLightImg, setIsLightImg] = React.useState(false);
    //原微波图像
    const [microImg, setMicroImg] = React.useState('');
    //是否选择了微波光图像
    const [isMicroImg, setIsMicroImg] = React.useState(false);
    //检测方法
    const [way, setWay] = React.useState(1);
    //是否选择了检测方法
    const [isWay, setIsWay] = React.useState(false);

    //处理后的可见光图像
    const [handleLightImg, setHandleLightImg] = React.useState('');
    //处理后的微波图像
    const [handleMicroImg, setHandleMicroImg] = React.useState('');
    //损伤类型和置信度数组
    const [data, setData] = React.useState([]);
    //表头
    const [header] = React.useState([
        {
            title: '损伤类型',
            dataIndex: 'type',
            key: 'type',
            className: 'text-monospace',
        }, {
            title: '置信度',
            dataIndex: 'confidence',
            key: 'confidence',
        }, {
            title: '框左上角坐标',
            dataIndex: 'left_top',
            key: 'left_top',
        }, {
            title: '框右下角坐标',
            dataIndex: 'right_bottom',
            key: 'right_bottom',
        }, {
            title: '检测时间',
            dataIndex: 'time',
            key: 'time',
        }
    ]);
    //表格内容
    const [excelList, setExcelList] = React.useState([{ type: '', confidence: '', left_top: '', right_bottom: '', time: '' }]);

    const lightProps = {
        name: 'file',
        multiple: false,
        accept: 'image/*',
        action: 'http://localhost:3000/api/upload',
        beforeUpload(file) {
            if (!isLightImg) {
                return true;
            }
            else {
                file.status = 'error';
                message.error('只支持上传一张图片', 1);
                return false;
            }
        },
        onChange(info) {
            const { status } = info.file;
            if (status !== 'uploading') {
                console.log(info.file, info.fileList);
            }
            if (status === 'done') {
                message.success(`${info.file.name} 上传成功.`, 1);
                axios.get(`http://localhost:3000/api/upload?type=image`).then(
                    response => {
                        setLightImg(response.data.originImg);
                        setIsLightImg(true);
                    },
                    error => {
                        console.log(error);
                    }
                )
            } else if (status === 'error') {
                message.error(`${info.file.name} 上传失败`, 1);
            }
        },
        onDrop(e) {
            console.log('Dropped files', e.dataTransfer.files);
        },
        onRemove(file) {
            if (file.status === 'done') {
                setIsLightImg(false);
                setLightImg('');
            }
        }
    };

    const microProps = {
        name: 'file',
        multiple: false,
        accept: 'image/*',
        action: 'http://localhost:3000/api/upload',
        beforeUpload(file) {
            if (!isMicroImg) {
                return true;
            }
            else {
                file.status = 'error';
                message.error('只支持上传一张图片', 1);
                return false;
            }
        },
        onChange(info) {
            const { status } = info.file;
            if (status !== 'uploading') {
                console.log(info.file, info.fileList);
            }
            if (status === 'done') {
                message.success(`${info.file.name} 上传成功.`, 1);
                axios.get(`http://localhost:3000/api/upload?type=image`).then(
                    response => {
                        setMicroImg(response.data.originImg);
                        setIsMicroImg(true);
                    },
                    error => {
                        console.log(error);
                    }
                )
            } else if (status === 'error') {
                message.error(`${info.file.name} 上传失败`, 1);
            }
        },
        onDrop(e) {
            console.log('Dropped files', e.dataTransfer.files);
        },
        onRemove(file) {
            if (file.status === 'done') {
                setIsMicroImg(false);
                setMicroImg('');
            }
        }
    };

    const callback = (key) => {
        console.log(key);
    }

    const columns = [
        {
            title: '损伤类型',
            dataIndex: 'type',
        },
        {
            title: '置信度',
            dataIndex: 'confidence',
            sorter: {
                compare: (a, b) => a.confidence - b.confidence,
                multiple: 6,
            },
            showSorterTooltip: false
        }
    ];

    const tableChange = (pagination, filters, sorter, extra) => {
        console.log('params', pagination, filters, sorter, extra);
    }


    const steps = [
        {
            title: '载入图像和选择检测方法',
            content: <div className={defectStyles.upload}>
                <Form
                    name="validate_other"
                    {...formItemLayout}
                    onFinish={onFinish}
                >
                    <Form.Item
                        name="upload"
                        label="载入可见光图像"
                        valuePropName="fileList"
                        getValueFromEvent={normFile}
                        extra=""
                    >
                        <Upload listType="picture" {...lightProps}>
                            <Button icon={<UploadOutlined />}>单击以上载</Button>
                        </Upload>
                    </Form.Item>

                    <Form.Item label="载入微波图像">
                        <Form.Item name="dragger" valuePropName="fileList" getValueFromEvent={normFile} noStyle>
                            <Upload.Dragger {...microProps}>
                                <p className="ant-upload-drag-icon">
                                    <InboxOutlined />
                                </p>
                                <p className="ant-upload-text">单击或拖动文件到此区域以上载</p>
                                <p className="ant-upload-hint">支持单个或批量上传。</p>
                            </Upload.Dragger>
                        </Form.Item>
                    </Form.Item>

                    <Form.Item name="radio-group" label="检测方法">
                        <Radio.Group onChange={handleWay}>
                            <Radio value={1}>可见光检测</Radio>
                            <Radio value={2}>双模态检测</Radio>
                        </Radio.Group>
                    </Form.Item>

                    <Form.Item wrapperCol={{ span: 12, offset: 6 }}>
                        {
                            (isLightImg && isMicroImg && isWay) ?
                                <Button type="primary" htmlType="submit">
                                    检测
                                </Button>
                                :
                                <Button type="primary" disabled>
                                    检测
                                </Button>
                        }
                    </Form.Item>
                </Form>
            </div>,
        },
        {
            title: '检测中',
            content: <div className={defectStyles.detecting}>
                <Progress type="circle" percent={percent} width={200} />
            </div>,
        },
        {
            title: '执行结果',
            content: <div className={defectStyles.result}>
                <Tabs defaultActiveKey="1" onChange={callback}>
                    <TabPane tab="滤波结果" key="1">
                        <div className={defectStyles.showImg}>
                            <div className={defectStyles.img3}>
                                <Image
                                    className={defectStyles.img}
                                    src={microImg}
                                    preview={{
                                        src: microImg,
                                    }}
                                    fallback="data:image/png;base64,"
                                />
                                <div className={defectStyles.name}>原微波图像</div>
                            </div>
                            <div className={defectStyles.img4}>
                                <Image
                                    className={defectStyles.img}
                                    src={handleMicroImg}
                                    preview={{
                                        src: handleMicroImg,
                                    }}
                                    fallback="data:image/png;base64,"
                                />
                                <div className={defectStyles.name}>滤波后的图像</div>
                            </div>
                        </div>
                    </TabPane>
                    <TabPane tab="检测结果" key="2">
                        <div className={defectStyles.showImg}>
                            <div className={defectStyles.img1}>
                                <Image
                                    className={defectStyles.img}
                                    src={lightImg}
                                    preview={{
                                        src: lightImg,
                                    }}
                                    fallback="data:image/png;base64,"
                                />
                                <div className={defectStyles.name}>原可见光图像</div>
                            </div>
                            <div className={defectStyles.img2}>
                                <Image
                                    className={defectStyles.img}
                                    src={handleLightImg}
                                    preview={{
                                        src: handleLightImg,
                                    }}
                                    fallback="data:image/png;base64,"
                                />
                                <div className={defectStyles.name}>处理后的可见光图像</div>
                            </div>
                        </div>
                    </TabPane>
                    <TabPane tab="损伤类型和置信度" key="3">
                        <Table className={defectStyles.table} columns={columns} dataSource={data} pagination={{ defaultPageSize: 3, defaultCurrent: 1 }} onChange={tableChange} bordered={true} />
                        <Button type="primary" className={defectStyles.result_btn} onClick={() => { exportExcel(header, excelList, "缺陷检测.xlsx") }}>
                            导出结果
                        </Button>
                    </TabPane>
                    <TabPane tab="损伤类型和置信度柱状图" key="4">
                        <Bar option={option} />
                    </TabPane>
                </Tabs>
            </div>,
        },
    ];

    const defectDete = () => {
        setCurrent(current + 1);
        axios.post('http://localhost:3000/api/defectDete', { lightImg, microImg, way }).then(
            response => {
                if (response.status === 200 && response.data.code === 1) {
                    message.success(response.data.msg, 1);
                    setHandleLightImg(response.data.handleLightImg);
                    setHandleMicroImg(response.data.handleMicroImg);
                    setData(response.data.data);
                    var result = response.data.data.map(item => {
                        return {
                            type: item.type,
                            confidence: item.confidence.toFixed(3),
                            left_top: `(${item.box[0].toFixed(2)}, ${item.box[1].toFixed(2)})`,
                            right_bottom: `(${item.box[2].toFixed(2)}, ${item.box[3].toFixed(2)})`,
                            time: item.time
                        }
                    });
                    setExcelList(result);
                    response.data.data.forEach(item => {
                        type.push(item['type']);
                        confidence.push(item['confidence']);
                    });
                }
                else
                    message.success(response.data.msg, 1);
            },
            error => {
                message.error(error, 1);
            }
        )
    }

    return (
        <div className={defectStyles.defectDete}>
            <div className={defectStyles.time}>
                <Time />
            </div>
            <Steps current={current}>
                {steps.map(item => (
                    <Step key={item.title} title={item.title} />
                ))}
            </Steps>
            <div className={defectStyles.steps_content}>{steps[current].content}</div>
            <div className={defectStyles.steps_action}>
                {current > 0 && (
                    <Button style={{ margin: '0 0.8rem' }} onClick={() => prev()}>
                        上一步
                    </Button>
                )}
                {current === steps.length - 2 && (
                    percent === 100 ?
                        <Button type="primary" onClick={() => setCurrent(current + 1)}>
                            完成
                        </Button>
                        :
                        <Button type="primary" disabled>
                            完成
                        </Button>
                )}
            </div>
        </div>
    );

}

export default connect()(withRouter(DefectDete));