import React from 'react';
import { Steps, Button, message, Upload, Progress, Descriptions, Image } from 'antd';
import { InboxOutlined } from '@ant-design/icons';
import { withRouter } from 'react-router-dom';
import { connect } from "react-redux";
import { exportExcel } from '../../utils/excel'
import axios from 'axios';
import Time from '../Time'
import identifyStyles from './index.module.less'

const { Dragger } = Upload;
const { Step } = Steps;

function Identify() {
    //步骤条
    const [current, setCurrent] = React.useState(0);
    //进度
    const [percent, setPercent] = React.useState(0);

    const next = () => {
        if (current === 0) {
            identify();
            setTimeout(() => {
                setPercent(50)
                setTimeout(() => {
                    setPercent(100);
                }, 800)
            }, 800);
        }
        setCurrent(current + 1);
    };

    const prev = () => {
        if (current === 2)
            setPercent(0);
        if (current === 1)
            setIsImg(false);
        setCurrent(current - 1);
    };

    //1.选择csv文件
    //原图像
    const [fileUrl, setFileUrl] = React.useState('');
    //是否选择了图像
    const [isImg, setIsImg] = React.useState(false);
    //数据
    const [data, setData] = React.useState('');
    //混淆矩阵
    const [matrix, setMatrix] = React.useState('');
    //识别结果
    const [result, setResult] = React.useState('');
    //表头
    const [header] = React.useState([
        {
            title: '数据',
            dataIndex: 'data',
            key: 'data',
            className: 'text-monospace',
        }, {
            title: '混淆矩阵',
            dataIndex: 'matrix',
            key: 'matrix',
        }, {
            title: '结果',
            dataIndex: 'result',
            key: 'result',
        }
    ]);
    //表格内容
    const [excelList, setExcelList] = React.useState([{ data: '', matrix: '', result: '' }]);

    const props = {
        name: 'file',
        multiple: false,
        accept: '.csv',
        action: 'http://localhost:3000/api/upload',
        beforeUpload(file) {
            if (!isImg) {
                return true;
            }
            else {
                file.status = 'error';
                message.error('只支持上传一个csv文件', 1);
                return false;
            }
        },
        onChange(info) {
            const { status } = info.file;
            if (status !== 'uploading') {
                console.log(info.file, info.fileList);
            }
            if (status === 'done') {
                message.success(`${info.file.name} 上传成功.`, 1);
                axios.get(`http://localhost:3000/api/upload?type=csv`).then(
                    response => {
                        setFileUrl(response.data.fileUrl);
                        setIsImg(true);
                    },
                    error => {
                        console.log(error);
                    }
                )
            } else if (status === 'error') {
                message.error(`${info.file.name} 上传失败`, 1);
            }
        },
        onDrop(e) {
            console.log('Dropped files', e.dataTransfer.files);
        },
        onRemove(file) {
            if (file.status === 'done') {
                setIsImg(false);
                setFileUrl('');
            }
        }
    };

    const steps = [
        {
            title: '选择文件',
            content: <div className={identifyStyles.upLoadFile}>
                <Dragger {...props}>
                    <p className="ant-upload-drag-icon">
                        <InboxOutlined />
                    </p>
                    <p className="ant-upload-text">单击或拖动文件到此区域以上载</p>
                    <p className="ant-upload-hint">
                        支持单个或批量上传。严禁上传公司数据或其他带文件
                    </p>
                </Dragger>
            </div>,
        },
        {
            title: '识别中',
            content: <Progress className={identifyStyles.progress} type="circle" percent={percent} width={200} />,
        },
        {
            title: '执行结果',
            content: <div className={identifyStyles.result}>
                <Descriptions className={identifyStyles.result_table} title="运行结果" layout="vertical" labelStyle={{ marginLeft: '9rem', fontWeight: 'bold' }} bordered>
                    <Descriptions.Item className={identifyStyles.result_item} label="数据">
                        <Image
                            className={identifyStyles.data}
                            src={data}
                            preview={{
                                src: data,
                            }}
                            fallback="data:image/png;base64,"
                        />
                    </Descriptions.Item>
                    <Descriptions.Item className={identifyStyles.result_item} label="混淆矩阵">
                        <Image
                            className={identifyStyles.matrix}
                            src={matrix}
                            preview={{
                                src: matrix,
                            }}
                            fallback="data:image/png;base64,"
                        />
                    </Descriptions.Item>
                    <Descriptions.Item className={identifyStyles.result_item} label="识别结果">{result}</Descriptions.Item>
                </Descriptions>
                <Button type="primary" className={identifyStyles.result_btn} onClick={() => { exportExcel(header, excelList, "信号识别.xlsx") }}>
                    导出结果
                </Button>
            </div>,
        },
    ];

    const identify = () => {
        setCurrent(current + 1);
        axios.post('http://localhost:3000/api/identify', { fileUrl }).then(
            response => {
                if (response.status === 200 && response.data.code === 1) {
                    message.success(response.data.msg, 1);
                    setData(response.data.data);
                    setMatrix(response.data.matrix);
                    setResult(response.data.result);
                    setExcelList([{
                        data: response.data.data,
                        matrix: response.data.matrix,
                        result: response.data.result
                    }]);
                }
                else
                    message.success(response.data.msg, 1);
            },
            error => {
                message.error(error, 1);
            }
        )
    }

    return (
        <div className={identifyStyles.identify}>
            <div className={identifyStyles.time}>
                <Time />
            </div>
            <Steps current={current}>
                {steps.map((item, index) => {
                    return <Step key={item.title} title={item.title} />
                })}
            </Steps>
            <div className={identifyStyles.steps_content}>{steps[current].content}</div>
            <div className={identifyStyles.steps_action}>
                {current > 0 && (
                    <Button style={{ margin: '0 0.8rem' }} onClick={() => prev()}>
                        上一步
                    </Button>
                )}
                {current < steps.length - 2 && (
                    isImg ?
                        <Button type="primary" onClick={() => next()}>
                            下一步
                        </Button>
                        :
                        <Button type="primary" disabled>
                            下一步
                        </Button>
                )}
                {current === steps.length - 2 && (
                    percent === 100 ?
                        <Button type="primary" onClick={() => next()}>
                            完成
                        </Button>
                        :
                        <Button type="primary" disabled>
                            完成
                        </Button>
                )}
            </div>
        </div>
    );

}

export default connect()(withRouter(Identify));