import React from 'react';
import { Steps, Button, message, Upload, Progress, Image } from 'antd';
import { InboxOutlined } from '@ant-design/icons';
import { withRouter } from 'react-router-dom';
import { connect } from "react-redux";
import axios from 'axios';
import Time from '../Time'
import perceptionStyles from './index.module.less'

const { Dragger } = Upload;
const { Step } = Steps;

function Perception() {
    //步骤条
    const [current, setCurrent] = React.useState(0);
    //进度
    const [percent, setPercent] = React.useState(0);

    const next = () => {
        if (current === 0) {
            perception();
            setPercent(100);
        }
        setCurrent(current + 1);
    };

    const prev = () => {
        if (current === 2)
            setPercent(0);
        if (current === 1)
            setIsImg(false);
        setCurrent(current - 1);
    };

    //1.选择文件
    //原图像
    const [originImg, setOriginImg] = React.useState('');
    //是否选择了图像
    const [isImg, setIsImg] = React.useState(false);
    //处理后的图像
    const [handleImg, setHandleImg] = React.useState('');

    const props = {
        name: 'file',
        multiple: false,
        accept: 'image/*',
        action: 'http://localhost:3000/api/upload',
        beforeUpload(file) {
            if(!isImg) {
                return true;
            }
            else {
                file.status = 'error';
                message.error('只支持上传一张图片', 1);
                return false;
            }
        },
        onChange(info) {
            const { status } = info.file;
            if (status !== 'uploading') {
                console.log(info.file, info.fileList);
            }
            if (status === 'done') {
                message.success(`${info.file.name} 上传成功.`, 1);
                axios.get(`http://localhost:3000/api/upload?type=image`).then(
                    response => {
                        setOriginImg(response.data.originImg);
                        setIsImg(true);
                    },
                    error => {
                        console.log(error);
                    }
                )
            } else if (status === 'error') {
                message.error(`${info.file.name} 上传失败`, 1);
            }
        },
        onDrop(e) {
            console.log('Dropped files', e.dataTransfer.files);
        },
        onRemove(file) {
            if(file.status === 'done') {
                setIsImg(false);
                setOriginImg('');
            }
        }
    };

    const steps = [
        {
            title: '选择文件',
            content: <div className={perceptionStyles.upLoadFile}>
                <Dragger {...props}>
                    <p className="ant-upload-drag-icon">
                        <InboxOutlined />
                    </p>
                    <p className="ant-upload-text">单击或拖动文件到此区域以上载</p>
                    <p className="ant-upload-hint">
                        支持单个或批量上传。严禁上传公司数据或其他带文件
                    </p>
                </Dragger>
            </div>,
        },
        {
            title: '感知中',
            content: <Progress className={perceptionStyles.progress} type="circle" percent={percent} width={200} />,
        },
        {
            title: '执行结果',
            content: <div className={perceptionStyles.result}>
                                <div className={perceptionStyles.showImg}>
                    <div className={perceptionStyles.originImg}>
                        <h2 className={perceptionStyles.title}>原图像</h2>
                        <Image
                            className={perceptionStyles.resultImg}
                            src={originImg}
                            preview={{
                                src: originImg,
                            }}
                            fallback="data:image/png;base64,"
                        />
                    </div>
                    <div className={perceptionStyles.handleImg}>
                        <h2 className={perceptionStyles.title}>显著性感知图像</h2>
                        <Image
                            className={perceptionStyles.resultImg}
                            src={handleImg}
                            preview={{
                                src: handleImg,
                            }}
                            fallback="data:image/png;base64,"
                        />
                    </div>
                </div>
            </div>,
        },
    ];

    const perception = () => {
        setCurrent(current + 1);
        axios.post('http://localhost:3000/api/perception', { originImg }).then(
            response => {
                if (response.status === 200 && response.data.code === 1) {
                    message.success(response.data.msg, 1);
                    setHandleImg(response.data.handleImg);
                }
                else
                    message.success(response.data.msg, 1);
            },
            error => {
                message.error(error, 1);
            }
        )
    }

    return (
        <div className={perceptionStyles.perception}>
            <div className={perceptionStyles.time}>
                <Time />
            </div>
            <Steps current={current}>
                {steps.map((item, index) => {
                    return <Step key={item.title} title={item.title} />
                })}
            </Steps>
            <div className={perceptionStyles.steps_content}>{steps[current].content}</div>
            <div className={perceptionStyles.steps_action}>
                {current > 0 && (
                    <Button style={{ margin: '0 0.8rem' }} onClick={() => prev()}>
                        上一步
                    </Button>
                )}
                {current < steps.length - 2 && (
                    isImg ?
                        <Button type="primary" onClick={() => next()}>
                            下一步
                        </Button>
                        :
                        <Button type="primary" disabled>
                            下一步
                        </Button>
                )}
                {current === steps.length - 2 && (
                    percent === 100 ?
                        <Button type="primary" onClick={() => next()}>
                            完成
                        </Button>
                        :
                        <Button type="primary" disabled>
                            完成
                        </Button>
                )}
            </div>
        </div>
    );

}

export default connect()(withRouter(Perception));